import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Logo from '../assets/logo.png';

const RegisterPage = ({ showAlert }) => {
  const navigate = useNavigate();

  const handleRegisterClick = () => {
    const isSuccess = true; // Simulate successful registration

    if (isSuccess) {
      showAlert('Registration successful!', 'success');
      navigate('/login'); // Redirect to login page after successful registration
    } else {
      showAlert('Registration failed. Email already exists.', 'error');
    }
  };

  return (
    <div className="flex items-center justify-center h-screen bg-cyan-200">
      <div className="w-4/6 max-w-md bg-white shadow-lg rounded-lg p-8 mx-4 flex flex-col items-center">
        
        <header className="mb-6 text-center">
          <img src={Logo} alt="logo" className="mb-4 h-56" />
          <h2 className="text-teal-700 text-3xl font-bold">
            SmartCommute</h2>
        </header>

        <h1 className="text-4xl font-bold text-teal-700 mb-6">
          Register</h1>
        <main className="w-full">
          <label className="block text-gray-700 text-left mb-2 text-lg">

            Enter your Username</label>
          <input type="text" placeholder="Username" className="w-full mb-4 p-3 border border-gray-300 rounded-lg" />
          <label className="block text-gray-700 text-left mb-2 text-lg">

            Enter your Email</label>
          <input type="email" placeholder="Email" className="w-full mb-4 p-3 border border-gray-300 rounded-lg" />
          <label className="block text-gray-700 text-left mb-2 text-lg">

            Enter your Password</label>
          <input type="password" placeholder="Password" className="w-full mb-6 p-3 border border-gray-300 rounded-lg" />
          <button className="w-full bg-teal-500 text-white py-3 px-6 rounded-lg font-bold text-lg hover:bg-teal-600 transition mb-4" 
          onClick={handleRegisterClick}>Register</button>
        </main>
      </div>
    </div>
  );
};

export default RegisterPage;
