import React, { useState, useEffect, useRef } from 'react';
import Alert from '../components/Alert'; 
import MapComponent from '../components/MapComponent'; 
import { useNavigate } from 'react-router-dom'; 
import axios from 'axios';
import {
  GoogleMap,
  useJsApiLoader,
  StandaloneSearchBox,
} from "@react-google-maps/api";

const API_KEY = process.env.REACT_APP_GMAPSAPI; // Adjust this according to your setup

const SearchPage = () => {
  const [startLocation, setStartLocation] = useState('');
  const [destination, setDestination] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [alert, setAlert] = useState({ show: false, message: '', type: '' });
  const [focusedInput, setFocusedInput] = useState(null);
  
  const startRef = useRef(null);
  const destinationRef = useRef(null);
  const navigate = useNavigate();

  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: API_KEY,
  });

  const fetchSuggestions = async (input) => {
    try {
      const response = await axios.get(
        `https://maps.googleapis.com/maps/api/place/autocomplete/json`,
        {
          params: {
            input,
            key: API_KEY,
            components: 'country:SG', // Restrict to Singapore
          },
        }
      );
      setSuggestions(response.data.predictions);
    } catch (error) {
      console.error('Error fetching location suggestions:', error);
    }
  };

  // Debounced filtering to improve performance
  useEffect(() => {
    if (focusedInput === 'start' && startLocation) {
      fetchSuggestions(startLocation);
    } else if (focusedInput === 'destination' && destination) {
      fetchSuggestions(destination);
    }
  }, [startLocation, destination, focusedInput]);

  const handleInputChange = (e, inputType) => {
    const value = e.target.value;
    if (inputType === 'start') {
      setStartLocation(value);
    } else {
      setDestination(value);
    }
    setFocusedInput(inputType);
  };

  const handleOnPlacesChanged = (inputType, searchBox) => {
    const places = searchBox.getPlaces();
    if (places.length > 0) {
      const place = places[0];
      if (inputType === 'start') {
        setStartLocation(place.formatted_address);
      } else {
        setDestination(place.formatted_address);
      }
      setSuggestions([]); // Clear suggestions after selection
    }
  };

  const handleSelectSuggestion = (suggestion, inputType) => {
    if (inputType === 'start') {
      setStartLocation(suggestion.description);
    } else {
      setDestination(suggestion.description);
    }
    setSuggestions([]);
    setFocusedInput(null);
  };

  const handleSearch = () => {
    if (!startLocation || !destination) {
      setAlert({ show: true, message: 'Please enter both starting location and destination.', type: 'error' });
      return;
    }

    setAlert({ show: true, message: 'Search initiated successfully!', type: 'success' });

    // Hide alert after 3 seconds automatically
    setTimeout(() => setAlert({ show: false, message: '', type: '' }), 3000);

    navigate('/comparison');
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gray-100">
      {alert.show && (
        <div className="absolute top-0 left-0 right-0 p-4">
          <Alert type={alert.type} message={alert.message} onClose={() => setAlert({ ...alert, show: false })} />
        </div>
      )}

      <main className="flex flex-col items-center mt-32 w-full max-w-lg">
        <div className="border border-gray-300 bg-gray-100 p-4 rounded-md mb-4">
          <p className="text-gray-600">Enter your starting and ending location</p>
        </div>

        {/* Starting Location Input */}
        <div className="w-full mb-4 relative">
          <label htmlFor="startLocation" className="block text-gray-700 text-lg font-medium mb-2">
            Starting Location
          </label>
          {isLoaded && (
            <StandaloneSearchBox
              onLoad={(ref) => (startRef.current = ref)}
              onPlacesChanged={() => handleOnPlacesChanged('start', startRef.current)}
            >
              <input
                type="text"
                placeholder="Enter starting location"
                className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500 shadow-sm"
                value={startLocation}
                onChange={(e) => handleInputChange(e, 'start')}
                onFocus={() => setFocusedInput('start')}
              />
            </StandaloneSearchBox>
          )}
          {/* Suggestions Dropdown */}
          {suggestions.length > 0 && focusedInput === 'start' && (
            <ul className="absolute z-10 bg-white border border-gray-300 rounded-lg shadow-lg w-full mt-1 max-h-60 overflow-y-auto">
              {suggestions.map((suggestion) => (
                <li
                  key={suggestion.place_id}
                  className="p-3 cursor-pointer hover:bg-gray-200 transition-colors"
                  onClick={() => handleSelectSuggestion(suggestion, 'start')}
                >
                  {suggestion.description}
                </li>
              ))}
            </ul>
          )}
        </div>

        {/* Destination Input */}
        <div className="w-full mb-4 relative">
          <label htmlFor="destination" className="block text-gray-700 text-lg font-medium mb-2">
            Destination
          </label>
          {isLoaded && (
            <StandaloneSearchBox
              onLoad={(ref) => (destinationRef.current = ref)}
              onPlacesChanged={() => handleOnPlacesChanged('destination', destinationRef.current)}
            >
              <input
                type="text"
                placeholder="Enter destination"
                className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500 shadow-sm"
                value={destination}
                onChange={(e) => handleInputChange(e, 'destination')}
                onFocus={() => setFocusedInput('destination')}
              />
            </StandaloneSearchBox>
          )}
          {/* Suggestions Dropdown */}
          {suggestions.length > 0 && focusedInput === 'destination' && (
            <ul className="absolute z-10 bg-white border border-gray-300 rounded-lg shadow-lg w-full mt-1 max-h-60 overflow-y-auto">
              {suggestions.map((suggestion) => (
                <li
                  key={suggestion.place_id}
                  className="p-3 cursor-pointer hover:bg-gray-200 transition-colors"
                  onClick={() => handleSelectSuggestion(suggestion, 'destination')}
                >
                  {suggestion.description}
                </li>
              ))}
            </ul>
          )}
        </div>

        {/* Search Button */}
        <button
          onClick={handleSearch}
          className="w-full bg-green-500 text-white py-3 px-6 rounded-lg font-semibold hover:bg-green-600 transition"
        >
          Search
        </button>
      </main>

      {/* Map with clickable points */}
      <div className="mt-10 w-full max-w-2xl h-96 bg-gray-200 rounded-lg shadow-md flex items-center justify-center relative">
        <MapComponent />
      </div>
    </div>
  );
};

export default SearchPage;
