import React, { useState, useRef, useEffect } from 'react';
import Alert from '../components/Alert'; 
import { useNavigate } from 'react-router-dom'; 
import { GoogleMap, useJsApiLoader, StandaloneSearchBox, Marker } from "@react-google-maps/api";

const API_KEY = process.env.REACT_APP_GMAPSAPI;
const libraries = ['places'];

const SearchPage = () => {
  const [map, setMap] = useState(null);
  const [markers, setMarkers] = useState([]);
  const [center, setCenter] = useState({ lat: 1.3521, lng: 103.8198 }); // Map view
  const [startLocation, setStartLocation] = useState('');
  const [destination, setDestination] = useState('');
  const [alert, setAlert] = useState({ show: false, message: '', type: '' });
  const [userLocation, setUserLocation] = useState(null); // State for user's GPS location

  const startRef = useRef(null);
  const destinationRef = useRef(null);
  const navigate = useNavigate();

  const { isLoaded } = useJsApiLoader({
    id: "google-map-script",
    googleMapsApiKey: API_KEY,
    libraries: libraries,
  });

  useEffect(() => {
    // Get user's current location
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setUserLocation({
            lat: position.coords.latitude,
            lng: position.coords.longitude,
            formatted_address: 'Your Current Location', // Displayed in dropdown
          });
        },
        (error) => {
          console.error('Error getting user location:', error);
        }
      );
    }
  }, []);

  const singaporeBounds = {
    north: 1.477,
    south: 1.15,
    east: 104.0945,
    west: 103.594,
  };

  const drawLineBetweenMarkers = (start, destination, map) => {
    if (start && destination) {
      const path = [
        new window.google.maps.LatLng(start.lat, start.lng),
        new window.google.maps.LatLng(destination.lat, destination.lng),
      ];
  
      const line = new window.google.maps.Polyline({
        path: path,
        geodesic: true,
        strokeColor: '#FF0000', // Color of the line
        strokeOpacity: 1.0,
        strokeWeight: 2, // Thickness of the line
      });
  
      line.setMap(map); // Set the line on the map
    }
  };

  const handleOnPlacesChanged = (inputType, ref) => {
    const places = ref.getPlaces();

    if (places && places.length > 0) {
      const place = places[0];

      if (place.geometry && place.geometry.location) {
        const location = place.geometry.location;

        const newMarker = {
          position: { lat: location.lat(), lng: location.lng() },
          title: inputType === 'start' ? 'Starting Location' : 'Destination',
        };

        if (inputType === 'start') {
          setStartLocation(place.formatted_address);
          setMarkers((prevMarkers) => {
            const updatedMarkers = prevMarkers.filter(marker => marker.title !== 'Starting Location');
            return [...updatedMarkers, newMarker];
          });
        } else {
          setDestination(place.formatted_address);
          setMarkers((prevMarkers) => {
            const updatedMarkers = prevMarkers.filter(marker => marker.title !== 'Destination');
            return [...updatedMarkers, newMarker];
          });
        }

        if (map) {
          map.setCenter(newMarker.position);
          if (inputType === 'start') {
            map.setZoom(14);
          }

          // Update line on each change of start or destination marker
          const startMarker = markers.find(marker => marker.title === 'Starting Location');
          const destinationMarker = markers.find(marker => marker.title === 'Destination');

          drawLineBetweenMarkers(
            startMarker ? startMarker.position : newMarker.position,
            destinationMarker ? destinationMarker.position : newMarker.position,
            map
          );
        }
      } else {
        setAlert({ show: true, message: 'Invalid location selected. Please choose from the dropdown.', type: 'error' });
      }
    } else {
      setAlert({ show: true, message: 'No locations found. Please try a different search.', type: 'error' });
    }
  };

  const handleSearch = () => {
    if (!startLocation || !destination) {
      setAlert({ show: true, message: 'Please enter both starting location and destination.', type: 'error' });
      return;
    }

    setAlert({ show: true, message: 'Search initiated successfully!', type: 'success' });
    setTimeout(() => setAlert({ show: false, message: '', type: '' }), 3000);
    navigate('/comparison');
  };

  return (
    <div className="flex w-full h-[calc(100vh-8rem)] overflow-hidden pt-20">
      {alert.show && (
        <div className="absolute top-0 left-0 right-0 p-4">
          <Alert type={alert.type} message={alert.message} onClose={() => setAlert({ ...alert, show: false })} />
        </div>
      )}

      {/* Left section (Map) */}
      <div className="w-3/5 h-full top-0">
        {isLoaded ? (
          <GoogleMap
            mapContainerStyle={{ width: '100%', height: '600px' }}
            zoom={12}
            center={center}
            onLoad={(mapInstance) => setMap(mapInstance)}
          >
            {markers.map((marker, index) => (
              <Marker key={index} position={marker.position} title={marker.title} />
            ))}
          </GoogleMap>
        ) : (
          <div>Loading....</div>
        )}
      </div>

      {/* Right section (Search bars) */}
      <div className="w-2/5 h-full flex items-center justify-center p-4">
        <div className="w-3/4 max-w-md max-h-full overflow-hidden flex flex-col justify-center">
          <div className="border border-gray-300 bg-gray-100 p-4 rounded-md mb-4 text-center">
            <p className="text-gray-600">Enter your starting and ending location</p>
          </div>

          <div className="w-full mb-4">
            <label htmlFor="startLocation" className="block text-gray-700 text-lg font-medium mb-2">
              Starting Location
            </label>
            {isLoaded && (
              <StandaloneSearchBox
                onLoad={(ref) => (startRef.current = ref)}
                onPlacesChanged={() => handleOnPlacesChanged('start', startRef.current)}
                options={{
                  componentRestrictions: { country: 'SG' },
                  strictBounds: true,
                }}
              >
                <input
                  type="text"
                  placeholder="Enter starting location"
                  className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500 shadow-sm"
                  onKeyDown={(e) => {
                    if (e.key === 'Enter') {
                      e.preventDefault(); 
                    }
                  }}
                  onChange={(e) => {
                    setStartLocation(e.target.value); // Update state on change
                    if (userLocation) {
                      // Add GPS location to dropdown options when user types
                      e.target.value = e.target.value === '' ? userLocation.formatted_address : e.target.value;
                    }
                  }} 
                />
              </StandaloneSearchBox>
            )}
          </div>

          <div className="w-full mb-4">
            <label htmlFor="destination" className="block text-gray-700 text-lg font-medium mb-2">
              Destination
            </label>
            {isLoaded && (
              <StandaloneSearchBox
                onLoad={(ref) => (destinationRef.current = ref)}
                onPlacesChanged={() => handleOnPlacesChanged('destination', destinationRef.current)}
                options={{
                  componentRestrictions: { country: 'SG' },
                  strictBounds: true,
                }}
              >
                <input
                  type="text"
                  placeholder="Enter destination"
                  className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500 shadow-sm"
                  onKeyDown={(e) => { e.key === 'Enter' && e.preventDefault(); }}
                  onChange={(e) => setDestination(e.target.value)} // Update state on change
                />
              </StandaloneSearchBox>
            )}
          </div>

          <button
            onClick={handleSearch}
            className="bg-blue-500 text-white font-semibold py-2 rounded-md hover:bg-blue-600 transition duration-200"
          >
            Search
          </button>
        </div>
      </div>
    </div>
  );
};

export default SearchPage;
