import React from 'react';
import { useLocation } from 'react-router-dom';
import Alert from './components/Alert';

const MainApp = () => {

  const location = useLocation();
  const alert = location.state?.alert; // Access the alert state passed during redirection

  return (
    
    <div className = "w-full bg-blue-100 min-h-screen ">

      {/* Login Alert */}
      {alert?.show && <Alert type={alert.type} message={alert.message} />}

      <nav className= "fixed top-0 left-0 bg-blue-400 py-4 text-white shadow-md z-40">
        <h1>Welcome to the Main Apn!</h1>
      </nav>
      
      
    </div>
  );
};

export default MainApp;
