import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import LandingPage from "./pages/LandingPage";
import LoginPage from "./pages/LoginPage";
import RegisterPage from "./pages/RegisterPage";
import MainApp from "./MainApp";
import Alert from "./components/Alert";

const App = () => {
  const [alert, setAlert] = useState({ show: false, message: '', type: '' });

  // Function to display alerts
  const showAlert = (message, type) => {
    setAlert({ show: true, message, type });
    setTimeout(() => setAlert({ show: false, message: '', type: '' }), 3000); // Auto-hide after 3 seconds
  };

  return (
    <Router>
      <div className="app-container">
        {/* Display the alert component if it's visible */}
        {alert.show && <Alert message={alert.message} type={alert.type} />}

        <Routes>
          <Route path="/" element={<LandingPage />} />
          <Route path="/login" element={<LoginPage showAlert={showAlert} />} />
          <Route path="/register" element={<RegisterPage showAlert={showAlert} />} />
          <Route path="/main" element={<MainApp />} />
          {/* Redirect any unknown route to the landing page */}
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
